import React, { useEffect, useRef } from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import 'bootstrap/dist/js/bootstrap.bundle.min';

export default function Header() {
  const navbarRef = useRef(null);

  useEffect(() => {
    const navbarItems = document.querySelectorAll('.nav-item');
    const handleNavItemClick = () => {
      const navbarToggler = document.querySelector('.navbar-toggler');
      if (navbarToggler && window.getComputedStyle(navbarToggler).display !== 'none') {
        const bsCollapse = new bootstrap.Collapse(navbarRef.current, { toggle: false });
        bsCollapse.hide();
      }
    };

    navbarItems.forEach(item => {
      item.addEventListener('click', handleNavItemClick);
    });

    return () => {
      navbarItems.forEach(item => {
        item.removeEventListener('click', handleNavItemClick);
      });
    };
  }, []);

  return (
    <nav className="navbar navbar-expand-sm bg-dark navbar-dark text-white">
      <div className="container-fluid">
        <Link className="navbar-brand" to="/">
          <img src="/vaibhav-gangrade-react-portfolio/images/vaibhav_gangrade.jpg" className="rounded-circle" height="50" width="50" alt="Vaibhav Gangrade" />
        </Link>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#collapsibleNavbar">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse justify-content-end" id="collapsibleNavbar" ref={navbarRef}>
          <ul className="navbar-nav">
            <li className="nav-item">
              <Link className="nav-link" to="/experience">Experience</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/education">Education</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/skills">Skills</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/projects">Projects</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/hobbies">Hobbies</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/achievements">Achievements</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/contactme">Contact Me</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/downloadcv">Download CV</Link>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
}
