import { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function DownloadCV() {
  const navigate = useNavigate();
  const [downloadCount, setDownloadCount] = useState(0);

  const handleDownload = () => {
    // Increase the download count when the button is clicked
    setDownloadCount((prevCount) => prevCount + 1);

    // Add your logic for the actual file download here
    // For example, you can redirect the user to the file URL
    navigate("/vaibhav-gangrade-react-portfolio/images/Vaibhav_Gangrade_web_Developer.docx");
  };

  return (
    <div className="container-fluid cv-container-fluid">
      <div className="container download_cv_container">
        <div className="row">
          <div className="col-md-4 education_div"></div>
          <div className="col-md-4 download_btn_div">
            <button onClick={handleDownload} className="btn btn-primary btn-lg download_cv">
              Download CV({downloadCount})
            </button>
            <span id="no_of_downloaded_cv"></span>
          </div>
          <div className="col-md-4 education_div"></div>
        </div>
      </div>
    </div>
  );
}
