import React from 'react';
import LiquidGauge from 'react-liquid-gauge';
import portfolio_data from './jsondata/portfolio_data';

function Skills() {
  const { skillsData } = portfolio_data;

  return (
    <div className="container-fluid software-skills-container-fluid">
      <div className="container">
        <h1>Skills</h1>
        <div className="row">
          {skillsData.map((skill, index) => (
            <div className="col-6 col-md-2 skill-section" key={index}>
              <div
                style={{
                  marginBottom: '10px',
                  position: 'relative',
                  width: '100%',
                  height: '0',
                  paddingBottom: '100%',
                  borderRadius: '50%',
                  border: `5px solid ${skill.circleColor}`
                }}
              >
                <LiquidGauge
                  width={120}
                  height={120}
                  value={skill.value}
                  percent="%" // Ensuring that the percent symbol is shown if required
                  textRenderer={() => (
                    <tspan>
                      <tspan className="value-text">{skill.label} ({skill.value}%)</tspan>
                    </tspan>
                  )}
                  riseAnimation
                  waveAnimation
                  waveFrequency={2}
                  waveAmplitude={1}
                  circleStyle={{
                    fill: skill.circleColor // Circle color
                  }}
                  waveStyle={{
                    fill: skill.waveColor // Wave color
                  }}
                />
              </div>
              <span>{skill.label}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Skills;
